import React, { Component } from 'react'; // Import React explicitly for older React versions
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      name: 'Mike'
    };
  }

  // Function to handle state updates
  toggleName = (newState) => {
    this.setState(newState);
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Hi {this.state.name}!</p>
          <button 
            onClick={() => {
              // Call the toggleName function with the appropriate object
              this.toggleName({
                name: this.state.name === 'Mike' ? 'John' : 'Mike' // Toggle between Mike and John
              });
            }}
          >
            Change Name
          </button>
        </header>
      </div>
    );
  }
}

export default App;
