import React, { Component } from 'react';
import './App.css';
import MonsterCard from './components/MonsterCard';

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [], // List of all monsters
      searchField: '', // Search input value
    };
  }

  componentDidMount() {
    // Fetch monsters from an API
    fetch('https://jsonplaceholder.typicode.com/users') // Example API
      .then((response) => response.json())
      .then((users) => this.setState({ monsters: users }))
      .catch((error) => console.error('Error fetching data:', error));
  }

  // Event handler for search input
  handleSearchChange = (event) => {
    this.setState({ searchField: event.target.value });
  };

  render() {
    // Filter monsters based on the search input
    const { monsters, searchField } = this.state;
    const filteredMonsters = monsters.filter((monster) =>
      monster.name.toLowerCase().includes(searchField.toLowerCase())
    );

    return (
      <div className="App">
        <header className="App-header">
          <h1>Monster Rolodex</h1>
          {/* Search bar */}
          <input
            type="search"
            placeholder="Search monsters"
            className="search-box"
            onChange={this.handleSearchChange} // Call the event handler on input change
          />
        </header>
        <div className="monster-container">
          {filteredMonsters.map((monster) => (
            <MonsterCard key={monster.id} monster={monster} />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
