import React, { Component } from 'react'; // Import React explicitly for older React versions
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      name: 'Mike'
    };

    // Array of random names
    this.names = ['Mike', 'John', 'Sarah', 'Emily', 'Chris', 'Jessica', 'David', 'Sophia'];
  }

  // Function to handle state updates with a random name
  changeToRandomName = () => {
    const randomName = this.names[Math.floor(Math.random() * this.names.length)];
    this.setState({ name: randomName });
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Hi {this.state.name}!</p>
          <button 
            onClick={this.changeToRandomName} // Call the function to change to a random name
          >
            Change Name
          </button>
        </header>
      </div>
    );
  }
}

export default App;
